        _queryFilter: "/supplierNumber eq \"" + newObject.frIndexedString2 + "\""
    };

    var organizationResponse = openidm.query("managed/alpha_organization", _queryFilter, ["*"]);
    var orgObj;
    if (organizationResponse.resultCount > 0) {
        orgObj = organizationResponse.result[0];
        if (newObject.frUnindexedString1 == "SUPPLIER-USER") {
            var operations = {
                operation: "add",
                field: "members/-",
                value: {_ref: "managed/alpha_user/" + newObject._id}
            };
            organization = openidm.patch("managed/alpha_organization/"+orgObj._id, null, [operations]);
        }
        else {
