postUpdate.source:
var oldRoles = oldObject.effectiveRoles;
var newRoles = newObject.effectiveRoles;
var currentDate = new Date();
var expDate = new Date();
expDate.setDate(currentDate.getDate() + 30);
var startDate = currentDate.toISOString();
var expirationDate = expDate.toISOString();
logger.error("Start date is {}, Expiration date is {}", startDate, expirationDate);
var tempDuration = `${startDate}/${expirationDate}`;
var tempRoles = [];
var foundLOARole = newRoles.find((role) => role._id === "55c50091-548c-45a4-b5f7-405d93c145c5" );
oldRoles.forEach((oldRole) => {
    logger.error("Old role found {}", JSON.stringify(oldRole));
    var foundRole = newRoles.find((role) => role._id === oldRole._id );
    if (!foundRole && !foundLOARole && oldRole._id !== "55c50091-548c-45a4-b5f7-405d93c145c5") {
        tempRoles.push(oldRole);
    }
});
tempRoles.forEach((tempRole) => {
    var operations = {
        operation: "add",
        field: "members/-",
        value: {
            _ref: "managed/alpha_user/" + newObject._id,
            _refProperties: {
                temporalConstraints: [
                    {
                        duration: tempDuration
                    }
                ]
            }
        }
    };
    logger.error("Temporary Role to be assigned is {}", JSON.stringify(tempRole));
    try {
        var tempMembershipObj = openidm.patch("managed/alpha_role/" + tempRole._id, null, [operations]);
        logger.error("TemporaryMembership successfully created with data {}", JSON.stringify(tempMembershipObj));
    }
    catch (e) {
        logger.error("Exception caught {}", e);
    }
});

