var companyName = object.companyName;

logger.error("In newSupplierOrg Script -- Supplier type object is {}", JSON.stringify(object.supplierType));
var supplierTypeObj = openidm.read(object.supplierType._ref, null, ["*"]);
var supplierTypeId = supplierTypeObj.id;

var newOrg = {
    supplierNumber: object.supplierNumber,
    name: companyName,
    supplierTypeID: supplierTypeId
};

var newOrgResult = openidm.create("managed/alpha_organization", null, newOrg);

logger.error("Inside newSupplierOrg script - OnCreate - new Org is {}", JSON.stringify(newOrgResult));

var operations = {
    operation: "replace",
    field: "linkedOrg",
    value: {
        _ref: "managed/alpha_organization/" + newOrgResult._id
    }
}

openidm.patch("managed/supcompany/" + newObject._id, null, [operations]);

